minispade.register('ember-runtime/~tests/suites/enumerable/invoke', "(function() {// ==========================================================================\n// Project:  Ember Runtime\n// Copyright: Â©2011 Strobe Inc. and contributors.\n// License:   Licensed under MIT license (see license.js)\n// ==========================================================================\nminispade.require('ember-runtime/~tests/suites/enumerable');\n\nvar suite = Ember.EnumerableTests;\n\nsuite.module('invoke');\n\nsuite.test('invoke should call on each object that implements', function() {\n  var cnt, ary, obj;\n\n  function F(amt) {\n    cnt += amt===undefined ? 1 : amt;\n  }\n  cnt = 0;\n  ary = [\n    { foo: F },\n    Ember.Object.create({ foo: F }),\n\n    // NOTE: does not impl foo - invoke should just skip\n    Ember.Object.create({ bar: F }),\n\n    { foo: F }\n  ];\n\n  obj = this.newObject(ary);\n  obj.invoke('foo');\n  equal(cnt, 3, 'should have invoked 3 times');\n\n  cnt = 0;\n  obj.invoke('foo', 2);\n  equal(cnt, 6, 'should have invoked 3 times, passing param');\n});\n\n\n\n})();\n//@ sourceURL=ember-runtime/~tests/suites/enumerable/invoke");