minispade.register('ember-handlebars/controls/text_support', "(function() {// ==========================================================================\n// Project:   Ember Handlebars Views\n// Copyright: Â©2011 Strobe Inc. and contributors.\n// License:   Licensed under MIT license (see license.js)\n// ==========================================================================\nminispade.require(\"ember-handlebars/ext\");\nminispade.require(\"ember-views/views/view\");\n\nvar get = Ember.get, set = Ember.set;\n\n/** @class */\nEmber.TextSupport = Ember.Mixin.create(\n/** @scope Ember.TextSupport.prototype */ {\n\n  value: \"\",\n\n  attributeBindings: ['placeholder', 'disabled', 'maxlength', 'tabindex'],\n  placeholder: null,\n  disabled: false,\n  maxlength: null,\n\n  insertNewline: Ember.K,\n  cancel: Ember.K,\n\n  /** @private */\n  init: function() {\n    this._super();\n    this.on(\"focusOut\", this, this._elementValueDidChange);\n    this.on(\"change\", this, this._elementValueDidChange);\n    this.on(\"keyUp\", this, this.interpretKeyEvents);\n  },\n\n  /**\n    @private\n  */\n  interpretKeyEvents: function(event) {\n    var map = Ember.TextSupport.KEY_EVENTS;\n    var method = map[event.keyCode];\n\n    this._elementValueDidChange();\n    if (method) { return this[method](event); }\n  },\n\n  _elementValueDidChange: function() {\n    set(this, 'value', this.$().val());\n  }\n\n});\n\nEmber.TextSupport.KEY_EVENTS = {\n  13: 'insertNewline',\n  27: 'cancel'\n};\n\n})();\n//@ sourceURL=ember-handlebars/controls/text_support");