minispade.register('ember-handlebars/helpers/unbound', "(function() {// ==========================================================================\n// Project:   Ember Handlebars Views\n// Copyright: Â©2011 Strobe Inc. and contributors.\n// License:   Licensed under MIT license (see license.js)\n// ==========================================================================\n/*globals Handlebars */\nminispade.require('ember-handlebars/ext');\n\nvar getPath = Ember.Handlebars.getPath;\n\n/**\n  `unbound` allows you to output a property without binding. *Important:* The\n  output will not be updated if the property changes. Use with caution.\n\n      <div>{{unbound somePropertyThatDoesntChange}}</div>\n\n  @name Handlebars.helpers.unbound\n  @param {String} property\n  @returns {String} HTML string\n*/\nEmber.Handlebars.registerHelper('unbound', function(property, fn) {\n  var context = (fn.contexts && fn.contexts[0]) || this;\n  return getPath(context, property, fn);\n});\n\n})();\n//@ sourceURL=ember-handlebars/helpers/unbound");