minispade.register('ember-metal/map', "(function() {/**\n  JavaScript (before ES6) does not have a Map implementation. Objects,\n  which are often used as dictionaries, may only have Strings as keys.\n\n  Because Ember has a way to get a unique identifier for every object\n  via `Ember.guidFor`, we can implement a performant Map with arbitrary\n  keys. Because it is commonly used in low-level bookkeeping, Map is\n  implemented as a pure JavaScript object for performance.\n\n  This implementation follows the current iteration of the ES6 proposal\n  for maps (http://wiki.ecmascript.org/doku.php?id=harmony:simple_maps_and_sets),\n  with two exceptions. First, because we need our implementation to be\n  pleasant on older browsers, we do not use the `delete` name (using\n  `remove` instead). Second, as we do not have the luxury of in-VM\n  iteration, we implement a forEach method for iteration.\n\n  Map is mocked out to look like an Ember object, so you can do\n  `Ember.Map.create()` for symmetry with other Ember classes.\n*/\nminispade.require('ember-metal/array');\nminispade.require('ember-metal/utils');\nminispade.require('ember-metal/core');\n\n/** @private */\nvar guidFor = Ember.guidFor,\n    indexOf = Ember.ArrayPolyfills.indexOf;\n\nvar copy = function(obj) {\n  var output = {};\n\n  for (var prop in obj) {\n    if (obj.hasOwnProperty(prop)) { output[prop] = obj[prop]; }\n  }\n\n  return output;\n};\n\nvar copyMap = function(original, newObject) {\n  var keys = original.keys.copy(),\n      values = copy(original.values);\n\n  newObject.keys = keys;\n  newObject.values = values;\n\n  return newObject;\n};\n\n// This class is used internally by Ember.js and Ember Data.\n// Please do not use it at this time. We plan to clean it up\n// and add many tests soon.\nvar OrderedSet = Ember.OrderedSet = function() {\n  this.clear();\n};\n\nOrderedSet.create = function() {\n  return new OrderedSet();\n};\n\nOrderedSet.prototype = {\n  clear: function() {\n    this.presenceSet = {};\n    this.list = [];\n  },\n\n  add: function(obj) {\n    var guid = guidFor(obj),\n        presenceSet = this.presenceSet,\n        list = this.list;\n\n    if (guid in presenceSet) { return; }\n\n    presenceSet[guid] = true;\n    list.push(obj);\n  },\n\n  remove: function(obj) {\n    var guid = guidFor(obj),\n        presenceSet = this.presenceSet,\n        list = this.list;\n\n    delete presenceSet[guid];\n\n    var index = indexOf.call(list, obj);\n    if (index > -1) {\n      list.splice(index, 1);\n    }\n  },\n\n  isEmpty: function() {\n    return this.list.length === 0;\n  },\n\n  has: function(obj) {\n    var guid = guidFor(obj),\n        presenceSet = this.presenceSet;\n\n    return guid in presenceSet;\n  },\n\n  forEach: function(fn, self) {\n    // allow mutation during iteration\n    var list = this.list.slice();\n\n    for (var i = 0, j = list.length; i < j; i++) {\n      fn.call(self, list[i]);\n    }\n  },\n\n  toArray: function() {\n    return this.list.slice();\n  },\n\n  copy: function() {\n    var set = new OrderedSet();\n\n    set.presenceSet = copy(this.presenceSet);\n    set.list = this.list.slice();\n\n    return set;\n  }\n};\n\n/**\n  A Map stores values indexed by keys. Unlike JavaScript's\n  default Objects, the keys of a Map can be any JavaScript\n  object.\n\n  Internally, a Map has two data structures:\n\n    `keys`: an OrderedSet of all of the existing keys\n    `values`: a JavaScript Object indexed by the\n      Ember.guidFor(key)\n\n  When a key/value pair is added for the first time, we\n  add the key to the `keys` OrderedSet, and create or\n  replace an entry in `values`. When an entry is deleted,\n  we delete its entry in `keys` and `values`.\n*/\n\n/** @private */\nvar Map = Ember.Map = function() {\n  this.keys = Ember.OrderedSet.create();\n  this.values = {};\n};\n\nMap.create = function() {\n  return new Map();\n};\n\nMap.prototype = {\n  /**\n    Retrieve the value associated with a given key.\n\n    @param {anything} key\n    @return {anything} the value associated with the key, or undefined\n  */\n  get: function(key) {\n    var values = this.values,\n        guid = guidFor(key);\n\n    return values[guid];\n  },\n\n  /**\n    Adds a value to the map. If a value for the given key has already been\n    provided, the new value will replace the old value.\n\n    @param {anything} key\n    @param {anything} value\n  */\n  set: function(key, value) {\n    var keys = this.keys,\n        values = this.values,\n        guid = guidFor(key);\n\n    keys.add(key);\n    values[guid] = value;\n  },\n\n  /**\n    Removes a value from the map for an associated key.\n\n    @param {anything} key\n    @returns {Boolean} true if an item was removed, false otherwise\n  */\n  remove: function(key) {\n    // don't use ES6 \"delete\" because it will be annoying\n    // to use in browsers that are not ES6 friendly;\n    var keys = this.keys,\n        values = this.values,\n        guid = guidFor(key),\n        value;\n\n    if (values.hasOwnProperty(guid)) {\n      keys.remove(key);\n      value = values[guid];\n      delete values[guid];\n      return true;\n    } else {\n      return false;\n    }\n  },\n\n  /**\n    Check whether a key is present.\n\n    @param {anything} key\n    @returns {Boolean} true if the item was present, false otherwise\n  */\n  has: function(key) {\n    var values = this.values,\n        guid = guidFor(key);\n\n    return values.hasOwnProperty(guid);\n  },\n\n  /**\n    Iterate over all the keys and values. Calls the function once\n    for each key, passing in the key and value, in that order.\n\n    The keys are guaranteed to be iterated over in insertion order.\n\n    @param {Function} callback\n    @param {anything} self if passed, the `this` value inside the\n      callback. By default, `this` is the map.\n  */\n  forEach: function(callback, self) {\n    var keys = this.keys,\n        values = this.values;\n\n    keys.forEach(function(key) {\n      var guid = guidFor(key);\n      callback.call(self, key, values[guid]);\n    });\n  },\n\n  copy: function() {\n    return copyMap(this, new Map());\n  }\n};\n\nvar MapWithDefault = Ember.MapWithDefault = function(options) {\n  Map.call(this);\n  this.defaultValue = options.defaultValue;\n};\n\nMapWithDefault.create = function(options) {\n  if (options) {\n    return new MapWithDefault(options);\n  } else {\n    return new Map();\n  }\n};\n\nMapWithDefault.prototype = Ember.create(Map.prototype);\n\nMapWithDefault.prototype.get = function(key) {\n  var hasValue = this.has(key);\n\n  if (hasValue) {\n    return Map.prototype.get.call(this, key);\n  } else {\n    var defaultValue = this.defaultValue(key);\n    this.set(key, defaultValue);\n    return defaultValue;\n  }\n};\n\nMapWithDefault.prototype.copy = function() {\n  return copyMap(this, new MapWithDefault({\n    defaultValue: this.defaultValue\n  }));\n};\n\n})();\n//@ sourceURL=ember-metal/map");