minispade.register('ember-runtime/mixins/copyable', "(function() {// ==========================================================================\n// Project:  Ember Runtime\n// Copyright: ©2006-2011 Strobe Inc. and contributors.\n//            Portions ©2008-2010 Apple Inc. All rights reserved.\n// License:   Licensed under MIT license (see license.js)\n// ==========================================================================\nminispade.require('ember-runtime/system/string');\n\n\n\nvar get = Ember.get, set = Ember.set;\n\n/**\n  @namespace\n\n  Implements some standard methods for copying an object.  Add this mixin to\n  any object you create that can create a copy of itself.  This mixin is\n  added automatically to the built-in array.\n\n  You should generally implement the copy() method to return a copy of the\n  receiver.\n\n  Note that frozenCopy() will only work if you also implement Ember.Freezable.\n\n  @extends Ember.Mixin\n  @since Ember 0.9\n*/\nEmber.Copyable = Ember.Mixin.create(\n/** @scope Ember.Copyable.prototype */ {\n\n  /**\n    Override to return a copy of the receiver.  Default implementation raises\n    an exception.\n\n    @function\n    @param deep {Boolean} if true, a deep copy of the object should be made\n    @returns {Object} copy of receiver\n  */\n  copy: Ember.required(Function),\n\n  /**\n    If the object implements Ember.Freezable, then this will return a new copy\n    if the object is not frozen and the receiver if the object is frozen.\n\n    Raises an exception if you try to call this method on a object that does\n    not support freezing.\n\n    You should use this method whenever you want a copy of a freezable object\n    since a freezable object can simply return itself without actually\n    consuming more memory.\n\n    @returns {Object} copy of receiver or receiver\n  */\n  frozenCopy: function() {\n    if (Ember.Freezable && Ember.Freezable.detect(this)) {\n      return get(this, 'isFrozen') ? this : this.copy().freeze();\n    } else {\n      throw new Error(Ember.String.fmt(\"%@ does not support freezing\", [this]));\n    }\n  }\n});\n\n\n\n\n})();\n//@ sourceURL=ember-runtime/mixins/copyable");