minispade.register('ember-runtime/system/application', "(function() {// ==========================================================================\n// Project:  Ember Runtime\n// Copyright: Â©2011 Strobe Inc. and contributors.\n// License:   Licensed under MIT license (see license.js)\n// ==========================================================================\nminispade.require('ember-runtime/system/namespace');\n\n/**\n  @private\n\n  Defines a namespace that will contain an executable application.  This is\n  very similar to a normal namespace except that it is expected to include at\n  least a 'ready' function which can be run to initialize the application.\n\n  Currently Ember.Application is very similar to Ember.Namespace.  However, this\n  class may be augmented by additional frameworks so it is important to use\n  this instance when building new applications.\n\n  # Example Usage\n\n      MyApp = Ember.Application.create({\n        VERSION: '1.0.0',\n        store: Ember.Store.create().from(Ember.fixtures)\n      });\n\n      MyApp.ready = function() {\n        //..init code goes here...\n      }\n\n*/\nEmber.Application = Ember.Namespace.extend();\n\n\n})();\n//@ sourceURL=ember-runtime/system/application");