minispade.register('ember-views/system/event_dispatcher', "(function() {// ==========================================================================\n// Project:   Ember - JavaScript Application Framework\n// Copyright: ©2006-2011 Strobe Inc. and contributors.\n//            Portions ©2008-2011 Apple Inc. All rights reserved.\n// License:   Licensed under MIT license (see license.js)\n// ==========================================================================\n\nvar get = Ember.get, set = Ember.set, fmt = Ember.String.fmt;\n\n/**\n  @ignore\n\n  Ember.EventDispatcher handles delegating browser events to their corresponding\n  Ember.Views. For example, when you click on a view, Ember.EventDispatcher ensures\n  that that view's `mouseDown` method gets called.\n*/\nEmber.EventDispatcher = Ember.Object.extend(\n/** @scope Ember.EventDispatcher.prototype */{\n\n  /**\n    @private\n\n    The root DOM element to which event listeners should be attached. Event\n    listeners will be attached to the document unless this is overridden.\n\n    Can be specified as a DOMElement or a selector string.\n\n    The default body is a string since this may be evaluated before document.body\n    exists in the DOM.\n\n    @type DOMElement\n    @default 'body'\n  */\n  rootElement: 'body',\n\n  /**\n    @private\n\n    Sets up event listeners for standard browser events.\n\n    This will be called after the browser sends a DOMContentReady event. By\n    default, it will set up all of the listeners on the document body. If you\n    would like to register the listeners on a different element, set the event\n    dispatcher's `root` property.\n  */\n  setup: function(addedEvents) {\n    var event, events = {\n      touchstart  : 'touchStart',\n      touchmove   : 'touchMove',\n      touchend    : 'touchEnd',\n      touchcancel : 'touchCancel',\n      keydown     : 'keyDown',\n      keyup       : 'keyUp',\n      keypress    : 'keyPress',\n      mousedown   : 'mouseDown',\n      mouseup     : 'mouseUp',\n      contextmenu : 'contextMenu',\n      click       : 'click',\n      dblclick    : 'doubleClick',\n      mousemove   : 'mouseMove',\n      focusin     : 'focusIn',\n      focusout    : 'focusOut',\n      mouseenter  : 'mouseEnter',\n      mouseleave  : 'mouseLeave',\n      submit      : 'submit',\n      input       : 'input',\n      change      : 'change',\n      dragstart   : 'dragStart',\n      drag        : 'drag',\n      dragenter   : 'dragEnter',\n      dragleave   : 'dragLeave',\n      dragover    : 'dragOver',\n      drop        : 'drop',\n      dragend     : 'dragEnd'\n    };\n\n    Ember.$.extend(events, addedEvents || {});\n\n    var rootElement = Ember.$(get(this, 'rootElement'));\n\n    Ember.assert(fmt('You cannot use the same root element (%@) multiple times in an Ember.Application', [rootElement.selector || rootElement[0].tagName]), !rootElement.is('.ember-application'));\n    Ember.assert('You cannot make a new Ember.Application using a root element that is a descendent of an existing Ember.Application', !rootElement.closest('.ember-application').length);\n    Ember.assert('You cannot make a new Ember.Application using a root element that is an ancestor of an existing Ember.Application', !rootElement.find('.ember-application').length);\n\n    rootElement.addClass('ember-application');\n\n    Ember.assert('Unable to add \"ember-application\" class to rootElement. Make sure you set rootElement to the body or an element in the body.', rootElement.is('.ember-application'));\n\n    for (event in events) {\n      if (events.hasOwnProperty(event)) {\n        this.setupHandler(rootElement, event, events[event]);\n      }\n    }\n  },\n\n  /**\n    @private\n\n    Registers an event listener on the document. If the given event is\n    triggered, the provided event handler will be triggered on the target\n    view.\n\n    If the target view does not implement the event handler, or if the handler\n    returns false, the parent view will be called. The event will continue to\n    bubble to each successive parent view until it reaches the top.\n\n    For example, to have the `mouseDown` method called on the target view when\n    a `mousedown` event is received from the browser, do the following:\n\n        setupHandler('mousedown', 'mouseDown');\n\n    @param {String} event the browser-originated event to listen to\n    @param {String} eventName the name of the method to call on the view\n  */\n  setupHandler: function(rootElement, event, eventName) {\n    var self = this;\n\n    rootElement.delegate('.ember-view', event + '.ember', function(evt, triggeringManager) {\n\n      var view = Ember.View.views[this.id],\n          result = true, manager = null;\n\n      manager = self._findNearestEventManager(view,eventName);\n\n      if (manager && manager !== triggeringManager) {\n        result = self._dispatchEvent(manager, evt, eventName, view);\n      } else if (view) {\n        result = self._bubbleEvent(view,evt,eventName);\n      } else {\n        evt.stopPropagation();\n      }\n\n      return result;\n    });\n\n    rootElement.delegate('[data-ember-action]', event + '.ember', function(evt) {\n      var actionId = Ember.$(evt.currentTarget).attr('data-ember-action'),\n          action   = Ember.Handlebars.ActionHelper.registeredActions[actionId],\n          handler  = action.handler;\n\n      if (action.eventName === eventName) {\n        return handler(evt);\n      }\n    });\n  },\n\n  /** @private */\n  _findNearestEventManager: function(view, eventName) {\n    var manager = null;\n\n    while (view) {\n      manager = get(view, 'eventManager');\n      if (manager && manager[eventName]) { break; }\n\n      view = get(view, 'parentView');\n    }\n\n    return manager;\n  },\n\n  /** @private */\n  _dispatchEvent: function(object, evt, eventName, view) {\n    var result = true;\n\n    var handler = object[eventName];\n    if (Ember.typeOf(handler) === 'function') {\n      result = handler.call(object, evt, view);\n      // Do not preventDefault in eventManagers.\n      evt.stopPropagation();\n    }\n    else {\n      result = this._bubbleEvent(view, evt, eventName);\n    }\n\n    return result;\n  },\n\n  /** @private */\n  _bubbleEvent: function(view, evt, eventName) {\n    return Ember.run(function() {\n      return view.handleEvent(eventName, evt);\n    });\n  },\n\n  /** @private */\n  destroy: function() {\n    var rootElement = get(this, 'rootElement');\n    Ember.$(rootElement).undelegate('.ember').removeClass('ember-application');\n    return this._super();\n  }\n});\n\n})();\n//@ sourceURL=ember-views/system/event_dispatcher");